    public boolean isPossible(int[] nums) {
        int len = nums.length;
        if(len<3) {
            return false;
        }
        Map<Integer, Integer> cnts = new HashMap<>();
        Map<Integer, Integer> needs = new HashMap<>();
        for(int num : nums) {
            cnts.put(num, cnts.getOrDefault(num, 0)+1);
        }
        for(int num : nums) {
            if(cnts.get(num)==0) {
                continue;
            }
            if(needs.getOrDefault(num, 0)>0) {
                needs.put(num, needs.get(num)-1);
                needs.put(num+1, needs.getOrDefault(num+1, 0)+1);
            }else if(cnts.getOrDefault(num+1, 0)>0&&cnts.getOrDefault(num+2, 0)>0) {
                cnts.put(num+1, cnts.get(num+1)-1);
                cnts.put(num+2, cnts.get(num+2)-1);
                needs.put(num+3, needs.getOrDefault(num+3, 0)+1);
            }else {
                return false;
            }
            cnts.put(num, cnts.get(num)-1);
        }
        return true;
    }

    public boolean isPossible1(int[] nums) {
        int len = nums.length;
        if(len<3) {
            return false;
        }
        int[][] mem = new int[len][2];
        int idx = 0;
        int[] count = new int[]{nums[0], 0};
        for(int i=0;i<len;i++) {
            if(nums[i]==count[0]) {
                count[1]++;
            }else {
                mem[idx++] = count;
                count = new int[]{nums[i], 1};
            }
        }
        mem[idx] = count;
        int left = 0;
        while(left<=idx) {
            while(left<=idx&&mem[left][1]==0) {
                left++;
            }
            if(left==idx+1) {
                return true;
            }
            int right = left+1;
            while(right<=idx&&mem[right][0]-mem[right-1][0]==1&&mem[right][1]>=mem[right-1][1]) {
                right++;
            }
            if(right-left<3) {
                return false;
            }else {
                for(int i=left;i<right;i++) {
                    mem[i][1] -= 1;
                }
            }
        }
        return true;
    }

